    render(){
        const post = this.state.post ? (
            <div  className = "post">
            <h4 className = "center">{this.state.post.title}</h4>
            <p>{this.state.post.body}</p>
            </div>

        ) : (
            <div>
            <h4 className = "center">"Loading..." </h4>
            </div>
        )
        return(
            <div className="container ">
                { post }
            </div>
        )
    }
}
export default Post;
